mapper.select:
SELECT
COUNT(1) AS TotalCount
FROM simulation_experiment.exercises;

SELECT A.exercises_id, A.system_id, C.system_name, A.course_id, D.course_name, A.knowledge_id, E.knowledge_name, A.exercises_type_id, F.exercises_type_name, A.experiment_type_id, G.experiment_type_name, A.exercises_content, B.full_name AS create_user, DATE_FORMAT(A.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, B.full_name AS update_user, DATE_FORMAT(A.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM simulation_experiment.exercises A LEFT JOIN simulation_experiment.system_users B ON A.create_user = B.user_id LEFT JOIN simulation_experiment.system_data C ON A.system_id = C.system_id LEFT JOIN simulation_experiment.course_data D ON A.course_id = D.course_id LEFT JOIN simulation_experiment.knowledge E ON A.knowledge_id = E.knowledge_id LEFT JOIN simulation_experiment.exercises_type_data F ON A.exercises_type_id = F.exercises_type_id LEFT JOIN simulation_experiment.experiment_type_data G ON A.experiment_type_id = G.experiment_type_id WHERE 1=1
AND A.system_id = #{arg2,jdbcType=INTEGER}
AND A.knowledge_id = #{arg3,jdbcType=INTEGER}
AND A.create_user = #{arg4,jdbcType=INTEGER}
ORDER BY A.update_time DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};
