mapper.select:
SELECT
COUNT(1) AS TotalCount
FROM simulation_experiment.teaching_resource;

SELECT A.resource_id, A.system_id, C.system_name, A.course_id, D.course_name, A.resource_type_id, E.source_type_name AS resource_type_name, REVERSE(LEFT(REVERSE(A.resource_uri),INSTR(REVERSE(A.resource_uri), '/') -1 )) AS resource_name, A.resource_uri, A.auditor_id, F.full_name AS auditor_name, A.resource_status, CASE WHEN A.resource_status = 'P' THEN '待审核' WHEN A.resource_status = 'A' THEN '审核通过' WHEN A.resource_status = 'R' THEN '审核未通过' ELSE '未知数据状态' END AS resource_status_text, B.full_name AS create_user, DATE_FORMAT(A.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, B.full_name AS update_user, DATE_FORMAT(A.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM simulation_experiment.teaching_resource A LEFT JOIN simulation_experiment.system_users B ON A.create_user = B.user_id LEFT JOIN simulation_experiment.system_data C ON A.system_id = C.system_id LEFT JOIN simulation_experiment.course_data D ON A.course_id = D.course_id LEFT JOIN simulation_experiment.resource_type_data E ON A.resource_type_id = E.source_type_id LEFT JOIN simulation_experiment.system_users F ON A.auditor_id = F.user_id WHERE 1=1
AND A.system_id = #{arg2,jdbcType=INTEGER}
AND A.course_id = #{arg3,jdbcType=INTEGER}
AND A.resource_type_id = #{arg4,jdbcType=INTEGER}
AND A.resource_status = #{arg5,jdbcType=INTEGER}
ORDER BY A.update_time DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

mapper.update:
UPDATE simulation_experiment.teaching_resource SET
  resource_status = #{resourceStatus,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE resource_id = #{resourceID,jdbcType=INTEGER}
